import React, { Component } from "react";
import NewsItem from "./NewsItem";
import Spinner from "./Spinner";
import PropTypes from 'prop-types'
import InfiniteScroll from "react-infinite-scroll-component";


export default class News extends Component {
  static defaulProps={
    country:'in',
    pageSize:'8',
    category:'general'
  }
  static propTypes={
    country:PropTypes.string,
    pageSize:PropTypes.number,
    category:PropTypes.string
  }
  constructor() {
    super();
    this.state = {
      articles: [],
      loading: true,
      page: 1,
      totalResults: 0,
    };
  }

  async fetchData() {
    try {
      this.setState({ loading: true });
      const response = await fetch(
        `https://newsapi.org/v2/top-headlines?country=${this.props.country}&category=${this.props.category}&apiKey=340d1fc4391e491abe887d277f6954dd&page=${page}&pageSize=${this.props.pageSize}`
      );
      const data = await response.json();
      this.setState({

        articles: data.articles,
        totalResults: data.totalResults,
        loading: false,

      });
    } catch (error) {
      console.error("Error fetching data", error);
    }
  }

  async componentDidMount() {
    this.fetchData();
  }

  handlePreviousClick = () => {
    if (this.state.page > 1) {
      this.fetchData(this.state.page - 1);
    }
  };

  handleNextClick = () => {
    if (this.state.page < Math.ceil(this.state.totalResults / this.props.pageSize)) {
      this.fetchData(this.state.page + 1);
    }
  };
  fetchMoreData = async() => {
      this.setState({
        page:this.state.page+1
      });
      try {
      this.setState({ loading: true });
      const response = await fetch(
        `https://newsapi.org/v2/top-headlines?country=${this.props.country}&category=${this.props.category}&apiKey=340d1fc4391e491abe887d277f6954dd&page=${page}&pageSize=${this.props.pageSize}`
      );
      const data = await response.json();
      this.setState({
        page:this.state.page,
        articles: this.state.articles.concat(data.articles),
        totalResults: data.totalResults,
        loading: false,
      });
    } catch (error) {
      console.error("Error fetching data", error);
    }
  };

  renderPagination() {
    const { page, totalResults } = this.state;
    const { pageSize } = this.props;

    const totalPages = Math.ceil(totalResults / pageSize);

    if (totalPages <= 1) return null;

    


    

    return (
      <div className="d-flex justify-content-between my-3">
        <button
          disabled={page <= 1}
          className="btn btn-sm btn-dark mx-1"
          onClick={this.handlePreviousClick}
        >
          &larr; Previous
        </button>

       

        <button
          disabled={page >= totalPages}
          className="btn btn-sm btn-dark mx-1"
          onClick={this.handleNextClick}
        >
          Next &rarr;
        </button>
      </div>
    );
  }

  render() {
    let { mode } = this.props;
    return (
      <div className="container my-3">
        <h1 className="text-center" style={{margin:'40px 0px'}}>News King - Top Headlines</h1>
        {/* {this.state.loading && <Spinner />} */}
        
          <InfiniteScroll
            dataLength={this.state.articles.length}
            next={this.fetchMoreData}
            hasMore={this.state.articles.length!==this.state.totalResults}
            loader={<Spinner/>}
            
          >
        <div className="row">
          {
            this.state.articles.map((element) => {
              return (
                <div className="col-md-4" key={element.url}>
                  <NewsItem
                    title={element.title ? element.title : ""}
                    description={element.description ? element.description : ""}
                    urlToImage={element.urlToImage}
                    url={element.url}
                    mode={mode}
                    author={element.author}
                    date={element.publishedAt}
                    source={element.source.name}
                  />
                </div>
              );
            })}
        </div>
        </InfiniteScroll>

        
      </div>
    );
  }
}
