import React, { useState, useEffect } from "react";
import NewsItem from "./NewsItem";
import Spinner from "./Spinner";
import PropTypes from "prop-types";
import InfiniteScroll from "react-infinite-scroll-component";

const News = ({ country='us' , pageSize='8', category='general' , mode, setProgress }) => {
  const [articles, setArticles] = useState([]);
  const [loading, setLoading] = useState(true);
  const [page, setPage] = useState(1);
  const [totalResults, setTotalResults] = useState(0);

  // ðŸ”¹ Fetch initial data on mount or when category/country changes
  useEffect(() => {
    const fetchData = async () => {
      setLoading(true);
      setProgress(10);
      

      try {
        const response = await fetch(
          `${process.env.REACT_APP_BACKEND_URL}?country=${country}&category=${category}&page=${page}&pageSize=${pageSize}`
        );
        setProgress(30);
        const data = await response.json();
        setArticles(data.articles || []);
        setTotalResults(data.totalResults || 0);
        setProgress(100);
      } catch (error) {
        console.error("Error fetching data", error);
      } finally {
        setLoading(false);
      }
    };

    fetchData();
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [category, country]); // re-fetch if category or country changes

  // ðŸ”¹ Fetch more data for infinite scroll
  const fetchMoreData = async () => {
    const nextPage = page + 1;
    setPage(nextPage);
    setLoading(true);
    try {
      const response = await fetch(
        `${process.env.REACT_APP_BACKEND_URL}?country=${country}&category=${category}&page=${nextPage}&pageSize=${pageSize}`
      );
      const data = await response.json();
      setArticles((prevArticles) => prevArticles.concat(data.articles || []));
      setTotalResults(data.totalResults || totalResults);
    } catch (error) {
      console.error("Error fetching more data", error);
    } finally {
      setLoading(false);
    }
  };

  return (
    <>
      <h1 className="text-center" style={{ margin: "40px 0px", marginTop: "90px" }}>
        NewsRa - Top Headlines
      </h1>

      {/* Show spinner only while first page is loading */}
      {loading && page === 1 && <Spinner mode={mode}/>}

      <InfiniteScroll
        dataLength={articles.length}
        next={fetchMoreData}
        hasMore={articles.length < totalResults}
        loader={loading && <Spinner mode={mode}/>}
      >
        <div className="container">
          <div className="row">
            {articles.map((element) => (
              <div className="col-md-4" key={element.url}>
                <NewsItem
                  title={element.title || ""}
                  description={element.description || ""}
                  urlToImage={element.urlToImage}
                  url={element.url}
                  mode={mode}
                  author={element.author}
                  date={element.publishedAt}
                  source={element.source.name}
                />
              </div>
            ))}
          </div>
        </div>
      </InfiniteScroll>
    </>
  );
};

// ðŸ”¹ Default Props
News.defaultProps = {
  country: "in",
  pageSize: 8,
  category: "general",
};

// ðŸ”¹ Prop Types
News.propTypes = {
  country: PropTypes.string,
  pageSize: PropTypes.number,
  category: PropTypes.string,
  apiKey: PropTypes.string.isRequired,
  mode: PropTypes.string.isRequired,
};

export default News;
