import React, { Component } from "react";
import NewsItem from "./NewsItem";
import Spinner from "./Spinner"

export default class News extends Component {
  
  constructor(){
    super();
    this.state={
      articles:[],
      loading:true,
      page:1,
      totalResults:0
    }
  }
  async componentDidMount(){
        try{
          this.setState({loading:true})
          const response=await fetch(`https://newsapi.org/v2/top-headlines?country=us&apiKey=340d1fc4391e491abe887d277f6954dd&page=1&pageSize=${this.props.pageSize}`);
          const data=await response.json();
          this.setState({articles:data.articles,totalResults:data.totalResults,loading:false});
        }
        catch(error){
          console.error("Error fetching data",error);
        }
  }
  handlePreviousClick=async()=>{
          this.setState({loading:true})
          const response=await fetch(`https://newsapi.org/v2/top-headlines?country=us&apiKey=340d1fc4391e491abe887d277f6954dd&page=${this.state.page-1}&pageSize=${this.props.pageSize}`);
          
          const data=await response.json();
          this.setState(
            {
              page:this.state.page-1,
              articles:data.articles,
              loading:false
            });
  }

  handleNextClick=async()=>{
         this.setState({loading:true})
          const response=await fetch(`https://newsapi.org/v2/top-headlines?country=us&apiKey=340d1fc4391e491abe887d277f6954dd&page=${this.state.page+1}&pageSize=${this.props.pageSize}`);
          const data=await response.json();
          this.setState(
            {
              page:this.state.page+1,
              articles:data.articles,
              loading:false
            });
          
  }
  render() {
    let {mode,pageSize}=this.props;
    return (
      <div className="container my-3">
        <h1 className="text-center">News King- Top Headlines</h1>
        {loading && <Spinner/>}
        <div className="row">
          {this.state.articles.map((element)=>{
          return <div className="col-md-4" key={element.url}>
            <NewsItem title={element.title?element.title:""} 
            description={element.description?element.description:""} 
            urlToImage={element.urlToImage} 
            url={element.url} mode={mode}/>
          </div>
        })}
        </div>
        <div class="d-flex justify-content-between">
          <button disabled={this.state.page<=1} type="button" class="btn btn-dark" onClick={this.handlePreviousClick}>&larr;Previous</button>
          <button disabled={this.state.page+1>Math.ceil(this.state.totalResults/pageSize)} type="button" class="btn btn-dark" onClick={this.handleNextClick}>Next&rarr;</button>
        </div>
      </div>
    );
  }
}
