import React, { Component } from 'react'

export default class NewsItem extends Component {
  render() {
    let {title,description,urlToImage,url,mode}=this.props;
    let myStyle={
      color: mode === "dark" ? "white" : "black",
      backgroundColor: mode === "dark" ? "black" : "white"
    }
    return (
      <div>
          <div className="card" style={{width: "18rem"}}>
            <img src={!urlToImage?"https://lh3.googleusercontent.com/proxy/2w6lCZLYuo6g2U9uZim4l8hN1CNdn-fbTKEvrxDnH1SBwzVft6evHWEbHIRRylb_qNy80UDQJmhGXAssLkO10Wziy0DJCL1K-zjHhFnSUlACPfp70F09O0Cj_WmNUci8L5f4kicX":urlToImage} className="card-img-top" alt="..."/>
            <div className="card-body" style={myStyle}>
                <h5 className="card-title">{title}...</h5>
                <p className="card-text">{description}...</p>
                <a href={url} target="_blank" rel="noreferrer"className="btn btn-dark">Read more</a>
            </div>
        </div>
      </div>
    )
  }
}
