import React, { Component } from 'react'
import samplePhoto from './SamplePhoto.jpg'

export default class NewsItem extends Component {
  render() {
    let {title,description,urlToImage,url,mode,author,date}=this.props;
    let myStyle={
      color: mode === "dark" ? "white" : "black",
      backgroundColor: mode === "dark" ? "black" : "white"
    }
    return (
      <div>
          <div className="card">
            <img src={!urlToImage?samplePhoto:urlToImage} className="card-img-top" alt="..."/>
            <div className="card-body" style={myStyle}>
                <h5 className="card-title">{title}...</h5>
                <p className="card-text">{description}...</p>
                <p class="card-text"><small class="text-body-secondary">By {author} on {new Date(date).toGMTString()}</small></p>
                <a href={url} target="_blank" rel="noreferrer" className={`btn btn-${mode==="dark"?"primary":"dark"}`}>Read more</a>
            </div>
        </div>
      </div>
    )
  }
}
