import React, { Component } from "react";
import NewsItem from "./NewsItem";


export default class News extends Component {
  
  constructor(){
    super();
    this.state={
      articles:[],
      loading:true,
      page:1,
      totalResults:0
    }
  }
  async componentDidMount(){
        try{
          const response=await fetch("https://newsapi.org/v2/top-headlines?country=us&apiKey=340d1fc4391e491abe887d277f6954dd&page=1&pageSize=20");
          const data=await response.json();
          this.setState({articles:data.articles,totalResults:data.articles.totalResults});
        }
        catch(error){
          console.error("Error fetching data",error);
        }
  }
  handlePreviousClick=async()=>{
          const response=await fetch(`https://newsapi.org/v2/top-headlines?country=us&apiKey=340d1fc4391e491abe887d277f6954dd&page=${this.state.page-1}&pageSize=20`);
          const data=await response.json();
          this.setState(
            {
              page:this.state.page-1,
              articles:data.articles
            });
  }

  handleNextClick=async()=>{
         if(this.state.page+1>Math.ceil(this.state.totalResults/20)){

         }else{
          const response=await fetch(`https://newsapi.org/v2/top-headlines?country=us&apiKey=340d1fc4391e491abe887d277f6954dd&page=${this.state.page+1}&pageSize=20`);
          const data=await response.json();
          this.setState(
            {
              page:this.state.page+1,
              articles:data.articles
            });
          }
  }
  render() {
    let {mode}=this.props;
    return (
      <div className="container my-3">
        <h2>News King- Top Headlines</h2>
        <div className="row">
          {this.state.articles.map((element)=>{
          return <div className="col-md-4" key={element.url}>
            <NewsItem title={element.title?element.title:""} 
            description={element.description?element.description:""} 
            urlToImage={element.urlToImage} 
            url={element.url} mode={mode}/>
          </div>
        })}
        </div>
        <div class="d-flex justify-content-between">
          <button disabled={this.state.page<=1}type="button" class="btn btn-dark" onClick={this.handlePreviousClick}>&larr;Previous</button>
          <button disabled={this.state.page+1>Math.ceil(this.state.totalResults/20)}type="button" class="btn btn-dark" onClick={this.handleNextClick}>Next&rarr;</button>
        </div>
      </div>
    );
  }
}
