import React, { Component } from "react";
import NewsItem from "./NewsItem";


export default class News extends Component {
  
  constructor(){
    super();
    this.state={
      articles:[],
      loading:false
    }
  }
  async componentDidMount(){
        try{
          const response=await fetch("https://newsapi.org/v2/top-headlines?apiKey=340d1fc4391e491abe887d277f6954dd&q=cricket");
          const data=await response.json();
          this.setState({articles:data.articles});
        }
        catch(error){
          console.error("Error fetching data",error);
        }
    }
  render() {
    return (
      <div className="container my-3">
        <h2>News King- Top Headlines</h2>
        <div className="row">
          {this.state.articles.map((element)=>{
          return <div className="col-md-4" key={element.url}>
            <NewsItem title={element.title?element.title.length<=45?element.title:element.title.slice(0,45):""} 
            description={element.description?element.description.length<=88?element.description:element.description.slice(0,88):""} 
            urlToImage={element.urlToImage} 
            url={element.url}/>
          </div>
  })}
        </div>
      </div>
    );
  }
}
