import "./App.css";

import React, { Component } from "react";
import Navbar from "./components/Navbar";
import News from "./components/News";
import { BrowserRouter as Router, Routes, Route } from "react-router-dom";


export default class App extends Component {
  constructor(){
    super();
    this.state={
      mode:"light"
    }
  }
  toggle=()=>{
    if(this.state.mode==="light"){
      this.setState({
        mode:"dark"
      });
      document.body.style.color="white";
      document.body.style.backgroundColor = "black";  
    }else{
      this.setState({
        mode:"light"
      });
      document.body.style.color="black";
      document.body.style.backgroundColor = "white";  
    }
  }
  pageSize=8;
  country="us";
  render() {
    return (
      <div>

        <Router>
          <Navbar mode={this.state.mode} toggle={this.toggle}/>

        <Routes>
            <Route exact path="/" key="general"element={<News mode={this.state.mode} pageSize={this.pageSize} country={this.country} category="general" />} />
            <Route exact path="/business" key="business"element={<News mode={this.state.mode} pageSize={this.pageSize} country={this.country} category="business" />} />
            <Route exact path="/entertainment" key="entertainment"element={<News mode={this.state.mode} pageSize={this.pageSize} country={this.country} category="entertainment" />} />
            <Route exact path="/health" key="health"element={<News mode={this.state.mode} pageSize={this.pageSize} country={this.country} category="health" />} />
            <Route exact path="/general" key="general"element={<News mode={this.state.mode} pageSize={this.pageSize} country={this.country} category="general" />} />
            <Route exact path="/science" key="science"element={<News mode={this.state.mode} pageSize={this.pageSize} country={this.country} category="science" />} />
            <Route exact path="/sports" key="sports"element={<News mode={this.state.mode} pageSize={this.pageSize} country={this.country} category="sports" />} />
            <Route exact path="/technology" key="technology"element={<News mode={this.state.mode} pageSize={this.pageSize} country={this.country} category="technology" />} />
      </Routes>
    </Router>
      </div>
    )
  }
}
