import React, { Component } from "react";
import NewsItem from "./NewsItem";


export default class News extends Component {
  
  constructor(){
    super();
    this.state={
      articles:[],
      loading:false,
      page:1
    }
  }
  async componentDidMount(){
        try{
          const response=await fetch("https://newsapi.org/v2/top-headlines?country=us&apiKey=340d1fc4391e491abe887d277f6954dd&page=1");
          const data=await response.json();
          this.setState({articles:data.articles});
        }
        catch(error){
          console.error("Error fetching data",error);
        }
  }
  
  handleNextClick=async()=>{
          const response=await fetch(`https://newsapi.org/v2/top-headlines?country=us&apiKey=340d1fc4391e491abe887d277f6954dd&page=${this.state.page+1}`);
          const data=await response.json();
          this.setState(
            {
              page:this.state.page+1,
              articles:data.articles
            });
  }

  render() {
    let {mode}=this.props;
    return (
      <div className="container my-3">
        <h2>News King- Top Headlines</h2>
        <div className="row">
          {this.state.articles.map((element)=>{
          return <div className="col-md-4" key={element.url}>
            <NewsItem title={element.title?element.title:""} 
            description={element.description?element.description:""} 
            urlToImage={element.urlToImage} 
            url={element.url} mode={mode}/>
          </div>
        })}
        </div>
        <div class="d-flex justify-content-between">
          <button type="button" class="btn btn-dark" onClick={handleOnChange}>&larr;Previous</button>
          <button type="button" class="btn btn-dark">Next&rarr;</button>
        </div>
      </div>
    );
  }
}
