import "./App.css";

import React, { Component } from "react";
import Navbar from "./components/Navbar";
import News from "./components/News";
import { BrowserRouter as Router, Routes, Route } from "react-router-dom";


export default class App extends Component {
  constructor(){
    super();
    this.state={
      mode:"light"
    }
  }
  toggle=()=>{
    if(this.state.mode==="light"){
      this.setState({
        mode:"dark"
      });
      document.body.style.color="white";
      document.body.style.backgroundColor = "black";  
    }else{
      this.setState({
        mode:"light"
      });
      document.body.style.color="black";
      document.body.style.backgroundColor = "white";  
    }
  }
  pageSize=8;
  country="us";
  apiKey=process.env.REACT_APP_API;
  render() {
    return (
      <div>

        <Router>
          <Navbar mode={this.state.mode} toggle={this.toggle}/>

        <Routes>
            <Route exact path="/" element={<News apiKey={this.apiKey} key="general" mode={this.state.mode} pageSize={this.pageSize} country={this.country} category="general" />} />
            <Route exact path="/business" element={<News apiKey={this.apiKey} key="business" mode={this.state.mode} pageSize={this.pageSize} country={this.country} category="business" />} />
            <Route exact path="/entertainment" element={<News apiKey={this.apiKey} key="entertainment" mode={this.state.mode} pageSize={this.pageSize} country={this.country} category="entertainment" />} />
            <Route exact path="/health" element={<News apiKey={this.apiKey} key="health" mode={this.state.mode} pageSize={this.pageSize} country={this.country} category="health" />} />
            <Route exact path="/general" element={<News apiKey={this.apiKey} key="general" mode={this.state.mode} pageSize={this.pageSize} country={this.country} category="general" />} />
            <Route exact path="/science" element={<News apiKey={this.apiKey} key="science" mode={this.state.mode} pageSize={this.pageSize} country={this.country} category="science" />} />
            <Route exact path="/sports" element={<News apiKey={this.apiKey} key="sports" mode={this.state.mode} pageSize={this.pageSize} country={this.country} category="sports" />} />
            <Route exact path="/technology" element={<News apiKey={this.apiKey} key="technology" mode={this.state.mode} pageSize={this.pageSize} country={this.country} category="technology" />} />
      </Routes>
    </Router>
      </div>
    )
  }
}
