import React, { Component } from "react";
import NewsItem from "./NewsItem";
import Spinner from "./Spinner";
import PropTypes from "prop-types";
import InfiniteScroll from "react-infinite-scroll-component";

export default class News extends Component {
  static defaultProps = {
    country: "in",
    pageSize: 8,
    category: "general",
  };

  static propTypes = {
    country: PropTypes.string,
    pageSize: PropTypes.number,
    category: PropTypes.string,
  };

  constructor() {
    super();
    this.state = {
      articles: [],
      loading: true,
      page: 1,
      totalResults: 0,
    };
  }

  async componentDidMount() {
    this.fetchData();
  }

  fetchData = async () => {
    this.setState({ loading: true });

    try {
      const response = await fetch(
        `https://newsapi.org/v2/top-headlines?country=${this.props.country}&category=${this.props.category}&apiKey=${this.props.apiKey}&page=${this.state.page}&pageSize=${this.props.pageSize}`
      );
      const data = await response.json();

      this.setState({
        articles: data.articles,
        totalResults: data.totalResults,
        loading: false,
      });
    } catch (error) {
      console.error("Error fetching data", error);
      this.setState({ loading: false });
    }
  };

  fetchMoreData = async () => {
    this.setState((prevState) => ({
      page: prevState.page + 1,
      loading: true,
    }));

    try {
      const response = await fetch(
        `https://newsapi.org/v2/top-headlines?country=${this.props.country}&category=${this.props.category}&apiKey=$&page=${this.state.page + 1}&pageSize=${this.props.pageSize}`
      );
      const data = await response.json();

      this.setState((prevState) => ({
        articles: prevState.articles.concat(data.articles),
        totalResults: data.totalResults,
        loading: false,
      }));
    } catch (error) {
      console.error("Error fetching data", error);
      this.setState({ loading: false });
    }
  };

  render() {
    let { mode } = this.props;

    return (
      <>
        <h1 className="text-center" style={{ margin: "40px 0px" }}>
          News King - Top Headlines
        </h1>

        {/* Show spinner only while first page is loading */}
        {this.state.loading && this.state.page === 1 && <Spinner />}

        <InfiniteScroll
          dataLength={this.state.articles.length}
          next={this.fetchMoreData}
          hasMore={this.state.articles.length < this.state.totalResults}
          loader={this.state.loading && <Spinner />}
        >
          <div className="container">
            <div className="row">
              {this.state.articles.map((element) => (
                <div className="col-md-4" key={element.url}>
                  <NewsItem
                    title={element.title ? element.title : ""}
                    description={element.description ? element.description : ""}
                    urlToImage={element.urlToImage}
                    url={element.url}
                    mode={mode}
                    author={element.author}
                    date={element.publishedAt}
                    source={element.source.name}
                  />
                </div>
              ))}
            </div>
          </div>
        </InfiniteScroll>
      </>
    );
  }
}
